'''
Given two strings s1 and s2, write a function to return true if s2 contains the permutation of s1. In other words, one of the first string's permutations is the substring of the second string.

Example 1:
Input:s1 = "ab" s2 = "eidbaooo"
Output:True
Explanation: s2 contains one permutation of s1 ("ba").
Example 2:
Input:s1= "ab" s2 = "eidboaoo"
Output: False
Note:
The input strings only contain lower case letters.
The length of both given strings is in range [1, 10,000]

'''

def two_dict_equal(dict1, dict2):
    for k, v in dict1.items():
        if v != dict2.get(k):
            return False
    return True


class Solution(object):
    def checkInclusion(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """
        perm_dict = {}
        count_dict = {}
        for char in s1:
            count = perm_dict.setdefault(char, 0)
            perm_dict[char] = count + 1
            count_dict[char] = 0

        start = 0
        end = len(s1) - 1

        # build initial count dict

        for char in s2[:end+1]:
            if char in count_dict:
                count_dict[char] += 1

        while end < len(s2):

            if start > 0:
                start_char = s2[start-1]
                end_char = s2[end]
                if start_char in count_dict:
                    count_dict[start_char] -= 1
                if end_char in count_dict:
                    count_dict[end_char] += 1
            if two_dict_equal(count_dict, perm_dict):
                return True
            start += 1
            end += 1
        return False




sol = Solution()
#s1= "ab"
#s2 = "eidbaoo"
#s1 = ""
#s2 = ""
s1 = "hello"
s2 = "ooolleoooleh"
print(sol.checkInclusion(s1, s2))